        editor.editor.applyTablePasteOptions('InsertAsRows');
        editor.editorHistory.undo();
        let table2: TableWidget = editor.documentHelper.pages[0].bodyWidgets[0].childWidgets[0] as TableWidget;
        let rowCount2: number = table2.childWidgets.length;
        expect(rowCount1).toEqual(rowCount2);
