    setVirtualHeight(height, width) {
        this.placeholder.style.height = `${height}px`;
        this.placeholder.style.width = width;
    }
    setFreezeWrapperWidth(wrapper, width, full) {
        wrapper.style.width = width ? `${width}px` : full ? '100%' : '';
    }
