import { PdfDocument, PdfGraphics, PdfPage, PdfColor, PdfPen } from './../../src/index';
import { PdfFont, PdfTrueTypeFont } from './../../src/index';
import { PdfGrid,  PdfSolidBrush, PdfNumberStyle, PdfGridRow } from './../../src/index';
import { RectangleF, PointF } from './../../src/index';
import { PdfPageNumberField, PdfDictionary } from './../../src/index';
import { PdfPageCountField } from './../../src/implementation/document/automatic-fields/page-count-field';
import { PdfCompositeField } from './../../src/implementation/document/automatic-fields/composite-field';
import { BeginPageLayoutEventArgs, PdfLayoutFormat, PdfLinearGradientBrush, PdfResources, PdfReferenceHolder, PdfStream, PdfNumber } from './../../src/index';
import { Utils } from './utils.spec';
describe('Stability issues', () => {
    it('849647-Linear gradient brush preservation issue', (done)=>{
        let document: PdfDocument = new PdfDocument();
        let page: PdfPage = document.pages.add();
        let graphics: PdfGraphics = page.graphics;
        let brush: PdfLinearGradientBrush = new PdfLinearGradientBrush(
            new PointF(0, 0),
            new PointF(200, 100),
            new PdfColor(255, 0, 0),
            new PdfColor(0, 0, 255)
        );
        graphics.drawRectangle(brush, 0, 0, 200, 100);
        document.save().then((xlBlob: { blobData: Blob }) => {
            if (Utils.isDownloadEnabled) {
                Utils.download(xlBlob.blobData, '849647-LinearGradientBrush.pdf');
            }
            let reader: FileReader = new FileReader();
            reader.readAsArrayBuffer(xlBlob.blobData);
            reader.onload = (): void => {
                if (reader.readyState == 2) { // DONE == 2
                    expect((reader.result as ArrayBuffer).byteLength).toBeGreaterThanOrEqual(0);
                    done();
                }
            }
        });
        let resources: PdfResources = page.getResources();
        let stream: PdfStream = ((((resources.getNames().keys()[0] as PdfDictionary).items.getValue('Shading') as PdfReferenceHolder).object as PdfDictionary).items.getValue('Function') as PdfReferenceHolder).element as PdfStream;
        expect(stream).not.toBeUndefined();
        expect(stream.data[0]).toEqual('ÿ\u0000\u0000\u0000\u0000ÿ');
        expect((stream.items.getValue('Length') as PdfNumber).intValue).toEqual(12);
        document.destroy();
    });
});
describe('EJ2_55800_FrameIssue',()=>{
    it('Frame preservation issue',(done)=>{
        //Begin cell draw Event Handler for drawing hyperlink in grid cell
        function beginPageLayoutFunction(
            sender: PdfGrid,
            e: BeginPageLayoutEventArgs
        ): void {
            e.page.graphics.drawRectangle(
                new PdfPen(new PdfColor(0, 0, 0), 10),
                10,
                10,
                e.page.graphics.clientSize.width - 20,
                e.page.graphics.clientSize.height - 20
            );
        }
        let document: PdfDocument = new PdfDocument();
        let page: PdfPage = document.pages.add();
        let pdfGrid: PdfGrid = new PdfGrid();
        pdfGrid.columns.add(2);
        pdfGrid.headers.add(1);
        let pdfGridHeader: PdfGridRow = pdfGrid.headers.getHeader(0);
        pdfGridHeader.cells.getCell(0).value = "ID";
        pdfGridHeader.cells.getCell(1).value = "Name";
        for (let i: number = 0; i < 100; i++) {
            let row1: PdfGridRow = pdfGrid.rows.addRow();
            row1.cells.getCell(0).value = "id_" + i.toString();
            row1.cells.getCell(1).value = "George";
        }
        let format: PdfLayoutFormat = new PdfLayoutFormat();
        format.paginateBounds = new RectangleF(20,
            20,
            page.graphics.clientSize.width - 40,
            page.graphics.clientSize.height - 40);
        pdfGrid.beginPageLayout = beginPageLayoutFunction;
        pdfGrid.draw(page, new RectangleF(20,
            20,
            page.graphics.clientSize.width - 40,
            page.graphics.clientSize.height - 40), format);

        document.save().then((xlBlob: { blobData: Blob }) => {
            if (Utils.isDownloadEnabled) {
                Utils.download(xlBlob.blobData, 'EJ2_55800_FrameIssue.pdf');
            }
            let reader: FileReader = new FileReader();
            reader.readAsArrayBuffer(xlBlob.blobData);
            reader.onload = (): void => {
                if (reader.readyState == 2) { // DONE == 2
                    expect((reader.result as ArrayBuffer).byteLength).toBeGreaterThanOrEqual(0);
                    done();
                }
            }
        });     
        document.destroy();  
    });
});

