describe('EJ2_46563_Undefined_PageNumber',()=> {
    it('Undefined error occurs while getting line width of text content on particular True Type Font', (done) => {
      //Create a new PDF document
      let document: PdfDocument = new PdfDocument();

      for (let i = 0; i < 10; i++) {
        document.pages.add();
      }
      let adventTTF: string =
        "";
      // set the font
      let font: PdfFont = new PdfTrueTypeFont(adventTTF, 12);
    
      // create black brush
      let blackBrush: PdfSolidBrush = new PdfSolidBrush(new PdfColor(0, 0, 0));
      let pageNumber: PdfPageNumberField = new PdfPageNumberField(font);
      pageNumber.numberStyle = PdfNumberStyle.Numeric;
      let pageCount: PdfPageCountField = new PdfPageCountField(font);
      pageCount.numberStyle = PdfNumberStyle.Numeric;
      // draw the text
      let compositeField: PdfCompositeField = new PdfCompositeField(
        font,
        blackBrush,
        "Page {0} of {1}",
        pageNumber,
        pageCount
      );
    
      for (let i = 0; i < 10; i++) {
        compositeField.draw(document.pages.getPageByIndex(i).graphics, 50, 40);
      }
    
      //Save the document.
      //document.save("EJ2_46563_Undefined_PageNumber.pdf");
      document.save().then((xlBlob: { blobData: Blob }) => {
          if (Utils.isDownloadEnabled) {
              Utils.download(xlBlob.blobData, 'EJ2_46563_Undefined_PageNumber.pdf');
          }
          let reader: FileReader = new FileReader();
          reader.readAsArrayBuffer(xlBlob.blobData);
          reader.onload = (): void => {
              if (reader.readyState == 2) { // DONE == 2
                  expect((reader.result as ArrayBuffer).byteLength).toBeGreaterThanOrEqual(0);
                  done();
              }
  
          }
      });
      document.destroy();
    });  
});
  